contracts/Account.sol.content:
// contracts/Account.sol
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "./AccountFactory.sol";

contract Account is Ownable, ReentrancyGuard {
    event Received(address from, uint256 amount);
    event Deposited(address from, uint256 amount, string method);
    event Withdrawn(address to, uint256 amount);
    event Transfer(address from, address to, uint256 amount, uint256 fee);
    event SavingsDeposited(uint256 amount, uint256 newBalance);
    event SavingsWithdrawn(uint256 amount, uint256 newBalance);

    address public immutable factoryAddress;
    uint256 public savingsBalance;
    
    // Maximum savings percentage to prevent locking all funds
    uint256 public constant MAX_SAVINGS_PERCENTAGE = 9000; // 90%

    constructor(address _owner, address _factoryAddress) Ownable(_owner) {
        require(_owner != address(0), "Invalid owner address");
        require(_factoryAddress != address(0), "Invalid factory address");
        factoryAddress = _factoryAddress;
    }

    // Transfer funds to another user's account
    function transfer(uint256 _amount, address _toUserWallet) external onlyOwner nonReentrant {
        require(_toUserWallet != address(0), "Invalid recipient address");
        require(_toUserWallet != msg.sender, "Cannot transfer to yourself");
        require(_amount > 0, "Amount must be greater than 0");
        
        AccountFactory factory = AccountFactory(factoryAddress);
        address recipientContractAddress = factory.userAccounts(_toUserWallet);
        require(recipientContractAddress != address(0), "Recipient account not found");
        
        uint256 fee = factory.transactionFee();
        uint256 totalRequired = _amount + fee;
        uint256 availableBalance = getAvailableBalance();
        
        require(totalRequired <= availableBalance, "Insufficient available balance");
        
        // Transfer fee to treasury
        payable(factory.treasury()).transfer(fee);
        // Transfer amount to recipient
        payable(recipientContractAddress).transfer(_amount);
        
        emit Transfer(msg.sender, _toUserWallet, _amount, fee);
    }

    // Move funds from main balance to savings
    function moveToSavings(uint256 _amount) external onlyOwner {
        require(_amount > 0, "Amount must be greater than 0");
        uint256 availableBalance = getAvailableBalance();
        require(_amount <= availableBalance, "Insufficient available balance");
        
        // Ensure savings doesn't exceed maximum percentage
        uint256 totalBalance = getBalance();
        uint256 newSavingsBalance = savingsBalance + _amount;
        require(
            newSavingsBalance <= (totalBalance * MAX_SAVINGS_PERCENTAGE) / 10000, 
            "Savings would exceed maximum percentage"
        );
        
        savingsBalance = newSavingsBalance;
        emit SavingsDeposited(_amount, savingsBalance);
    }

    // Withdraw funds from savings to main balance
    function withdrawFromSavings(uint256 _amount) external onlyOwner {
        require(_amount > 0, "Amount must be greater than 0");
        require(_amount <= savingsBalance, "Insufficient savings balance");
        
        savingsBalance -= _amount;
        emit SavingsWithdrawn(_amount, savingsBalance);
    }

    // Deposit funds into the account
    function deposit() external payable onlyOwner {
        require(msg.value > 0, "Must send ETH to deposit");
        emit Deposited(msg.sender, msg.value, "deposit()");
    }

    // Withdraw funds from the account
    function withdraw(uint256 _amount) external onlyOwner nonReentrant {
        require(_amount > 0, "Amount must be greater than 0");
        uint256 availableBalance = getAvailableBalance();
        require(_amount <= availableBalance, "Insufficient available balance");
        
        payable(owner()).transfer(_amount);
        emit Withdrawn(owner(), _amount);
    }

    // Emergency withdrawal of all available funds
    function emergencyWithdraw() external onlyOwner nonReentrant {
        uint256 availableBalance = getAvailableBalance();
        require(availableBalance > 0, "No funds available");
        
        payable(owner()).transfer(availableBalance);
        emit Withdrawn(owner(), availableBalance);
    }

    // Get total balance of the contract
    function getBalance() public view returns (uint256) {
        return address(this).balance;
    }

    // Get available balance (total - savings)
    function getAvailableBalance() public view returns (uint256) {
        uint256 totalBalance = getBalance();
        return totalBalance > savingsBalance ? totalBalance - savingsBalance : 0;
    }

    // Get savings balance
    function getSavingsBalance() external view returns (uint256) {
        return savingsBalance;
    }

    // Receive function for incoming ETH
    receive() external payable {
        emit Received(msg.sender, msg.value);
    }

    // Fallback function
    fallback() external payable {
        emit Received(msg.sender, msg.value);
    }
}

contracts/AccountFactory.sol.content:
// contracts/AccountFactory.sol
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.20;

import "./Account.sol";

contract AccountFactory {
    address public treasury;
    uint256 public transactionFee = 0.0001 ether;

    constructor() {
        treasury = msg.sender;
    }

    event AccountCreated(address indexed owner, address indexed accountAddress, string username);
    event TreasuryUpdated(address indexed oldTreasury, address indexed newTreasury);
    event TransactionFeeUpdated(uint256 oldFee, uint256 newFee);

    mapping(address => address) public userAccounts;
    mapping(string => address) public usernameToAddress;
    mapping(address => string) public addressToUsername;

    modifier onlyTreasury() {
        require(msg.sender == treasury, "Only treasury can call this function");
        _;
    }

    // Create a new Account contract for the user
    function createAccount(string calldata _username) external {
        require(userAccounts[msg.sender] == address(0), "User already has an account");
        require(usernameToAddress[_username] == address(0), "Username is already taken");
        require(bytes(_username).length > 0, "Username cannot be empty");
        require(bytes(_username).length <= 32, "Username too long");
        
        Account newAccount = new Account(msg.sender, address(this));
        userAccounts[msg.sender] = address(newAccount);
        usernameToAddress[_username] = msg.sender;
        addressToUsername[msg.sender] = _username;
        
        emit AccountCreated(msg.sender, address(newAccount), _username);
    }

    // Get user's account address
    function getMyAccountAddress(address _user) external view returns (address) {
        return userAccounts[_user];
    }

    // Get username by address
    function getUsername(address _user) external view returns (string memory) {
        return addressToUsername[_user];
    }

    // Update treasury address (only current treasury)
    function updateTreasury(address _newTreasury) external onlyTreasury {
        require(_newTreasury != address(0), "Invalid treasury address");
        address oldTreasury = treasury;
        treasury = _newTreasury;
        emit TreasuryUpdated(oldTreasury, _newTreasury);
    }

    // Update transaction fee (only treasury)
    function updateTransactionFee(uint256 _newFee) external onlyTreasury {
        require(_newFee <= 0.01 ether, "Fee too high"); // Max 0.01 ETH
        uint256 oldFee = transactionFee;
        transactionFee = _newFee;
        emit TransactionFeeUpdated(oldFee, _newFee);
    }

    // Check if user has an account
    function hasAccount(address _user) external view returns (bool) {
        return userAccounts[_user] != address(0);
    }
}

contracts/Lending.sol.content:
// contracts/lending.sol
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@chainlink/contracts/src/v0.8/shared/interfaces/AggregatorV3Interface.sol";

contract DETH is ERC20, Ownable {
    address public minter;

    constructor() ERC20("Deposited Ether", "dETH") Ownable(msg.sender) {}

    function setMinter(address _minter) external onlyOwner {
        require(minter == address(0), "Minter already set");
        require(_minter != address(0), "Invalid minter address");
        minter = _minter;
    }

    function mint(address to, uint256 amount) external {
        require(msg.sender == minter, "Only minter can mint");
        require(to != address(0), "Cannot mint to zero address");
        _mint(to, amount);
    }

    function burn(address from, uint256 amount) external {
        require(msg.sender == minter, "Only minter can burn");
        require(from != address(0), "Cannot burn from zero address");
        _burn(from, amount);
    }
}

contract Lending is Ownable, ReentrancyGuard {
    using SafeERC20 for IERC20;
    
    DETH public immutable dETHToken;
    uint256 public constant LTV_BPS = 8000; // 80%
    uint256 public constant LIQ_THRESHOLD_BPS = 8500; // 85%
    uint256 public constant LIQ_BONUS_BPS = 10500; // 105%
    uint256 public constant PRECISION = 1e18;
    uint256 private constant PRICE_TIMEOUT = 3600; // 1 hour

    mapping(address => bool) public supportedCollateral;
    mapping(address => AggregatorV3Interface) public priceFeed;
    mapping(address => mapping(address => uint256)) public collateralBalance;
    mapping(address => uint256) public borrowBalances;
    
    address[] public collateralList;
    AggregatorV3Interface public immutable ethUsdFeed;

    event DepositedETH(address indexed user, uint256 amount, uint256 dETHMinted);
    event WithdrawnETH(address indexed user, uint256 dETHBurned, uint256 amount);
    event CollateralSupported(address indexed token, address indexed feed);
    event CollateralDeposited(address indexed user, address indexed token, uint256 amount);
    event CollateralWithdrawn(address indexed user, address indexed token, uint256 amount);
    event Borrowed(address indexed user, uint256 amount);
    event Repaid(address indexed user, uint256 repaid, uint256 remainingDebt);
    event Liquidated(
        address indexed liquidator, 
        address indexed user, 
        uint256 repaid, 
        uint256 collateralSeized
    );

    constructor(address _dETH, address _ethUsdFeed) Ownable(msg.sender) {
        require(_dETH != address(0), "Invalid dETH address");
        require(_ethUsdFeed != address(0), "Invalid ETH USD feed address");
        
        dETHToken = DETH(_dETH);
        ethUsdFeed = AggregatorV3Interface(_ethUsdFeed);
    }

    // Set supported collateral with price feed
    function setSupportedCollateral(
        address token, 
        bool supported, 
        address tokenUsdFeed
    ) external onlyOwner {
        require(token != address(0), "Invalid token address");
        
        if (supported) {
            require(tokenUsdFeed != address(0), "Invalid price feed address");
            
            if (!supportedCollateral[token]) {
                collateralList.push(token);
            }
            
            supportedCollateral[token] = true;
            priceFeed[token] = AggregatorV3Interface(tokenUsdFeed);
            emit CollateralSupported(token, tokenUsdFeed);
        } else {
            supportedCollateral[token] = false;
            priceFeed[token] = AggregatorV3Interface(address(0));
        }
    }

    // Deposit ETH to get dETH tokens
    function depositInPool() external payable nonReentrant {
        require(msg.value > 0, "Must send ETH");
        
        uint256 totalSupply = dETHToken.totalSupply();
        uint256 poolBefore = address(this).balance - msg.value;
        
        uint256 mintAmount;
        if (totalSupply == 0 || poolBefore == 0) {
            mintAmount = msg.value;
        } else {
            mintAmount = (msg.value * totalSupply) / poolBefore;
        }
        
        dETHToken.mint(msg.sender, mintAmount);
        emit DepositedETH(msg.sender, msg.value, mintAmount);
    }

    // Withdraw ETH by burning dETH tokens
    function withdrawFromPool(uint256 dETHAmount) external nonReentrant {
        require(dETHAmount > 0, "Amount must be greater than 0");
        require(dETHToken.balanceOf(msg.sender) >= dETHAmount, "Insufficient dETH balance");
        
        uint256 totalSupply = dETHToken.totalSupply();
        require(totalSupply > 0, "Empty pool");
        
        uint256 poolETH = address(this).balance;
        uint256 ethOut = (dETHAmount * poolETH) / totalSupply;
        
        require(ethOut > 0, "No ETH to withdraw");
        
        dETHToken.burn(msg.sender, dETHAmount);
        _safeTransferETH(msg.sender, ethOut);
        
        emit WithdrawnETH(msg.sender, dETHAmount, ethOut);
    }

    // Deposit collateral tokens
    function depositCollateral(address token, uint256 amount) external nonReentrant {
        require(supportedCollateral[token], "Collateral not supported");
        require(amount > 0, "Amount must be greater than 0");
        
        IERC20(token).safeTransferFrom(msg.sender, address(this), amount);
        collateralBalance[msg.sender][token] += amount;
        
        emit CollateralDeposited(msg.sender, token, amount);
    }

    // Withdraw collateral tokens
    function withdrawCollateral(address token, uint256 amount) external nonReentrant {
        require(supportedCollateral[token], "Collateral not supported");
        require(amount > 0, "Amount must be greater than 0");
        require(collateralBalance[msg.sender][token] >= amount, "Insufficient collateral balance");
        
        // Check that withdrawal won't make position unhealthy
        uint256 currentDebt = borrowBalances[msg.sender];
        if (currentDebt > 0) {
            // Simulate withdrawal
            collateralBalance[msg.sender][token] -= amount;
            require(getHealthFactor(msg.sender) >= PRECISION, "Withdrawal would make position unhealthy");
            collateralBalance[msg.sender][token] += amount; // Restore for actual withdrawal
        }
        
        collateralBalance[msg.sender][token] -= amount;
        IERC20(token).safeTransfer(msg.sender, amount);
        
        emit CollateralWithdrawn(msg.sender, token, amount);
    }

    // Borrow ETH against collateral
    function borrow(uint256 amountWei) external nonReentrant {
        require(amountWei > 0, "Amount must be greater than 0");
        require(address(this).balance >= amountWei, "Insufficient pool balance");
        
        uint256 collateralValueETH = _userCollateralValueInETH(msg.sender);
        require(collateralValueETH > 0, "No collateral deposited");
        
        uint256 maxBorrow = (collateralValueETH * LTV_BPS) / 10000;
        uint256 newTotalDebt = borrowBalances[msg.sender] + amountWei;
        
        require(newTotalDebt <= maxBorrow, "Exceeds borrowing capacity");
        
        borrowBalances[msg.sender] = newTotalDebt;
        _safeTransferETH(msg.sender, amountWei);
        
        emit Borrowed(msg.sender, amountWei);
    }

    // Repay borrowed ETH
    function repay() external payable nonReentrant {
        require(msg.value > 0, "Must send ETH");
        
        uint256 debt = borrowBalances[msg.sender];
        require(debt > 0, "No debt to repay");
        
        uint256 toApply = msg.value > debt ? debt : msg.value;
        borrowBalances[msg.sender] = debt - toApply;

        // Refund excess payment
        if (msg.value > toApply) {
            _safeTransferETH(msg.sender, msg.value - toApply);
        }
        
        emit Repaid(msg.sender, toApply, borrowBalances[msg.sender]);
    }

    // Liquidate unhealthy position
    function liquidate(address user) external payable nonReentrant {
        require(user != address(0), "Invalid user address");
        require(user != msg.sender, "Cannot liquidate yourself");
        
        uint256 debt = borrowBalances[user];
        require(debt > 0, "User has no debt");
        require(getHealthFactor(user) < PRECISION, "Position is healthy");
        require(msg.value >= debt, "Insufficient payment for liquidation");

        // Clear user's debt
        borrowBalances[user] = 0;
        
        // Refund excess payment
        if (msg.value > debt) {
            _safeTransferETH(msg.sender, msg.value - debt);
        }

        // Seize collateral with bonus
        uint256 totalSeized = 0;
        for (uint256 i = 0; i < collateralList.length; i++) {
            address token = collateralList[i];
            if (!supportedCollateral[token]) continue;
            
            uint256 balance = collateralBalance[user][token];
            if (balance == 0) continue;
            
            collateralBalance[user][token] = 0;
            IERC20(token).safeTransfer(msg.sender, balance);
            totalSeized += _tokenValueInETH(token, balance);
        }
        
        emit Liquidated(msg.sender, user, debt, totalSeized);
    }

    // Get user's health factor (1e18 = 100%)
    function getHealthFactor(address user) public view returns (uint256) {
        uint256 debt = borrowBalances[user];
        if (debt == 0) return type(uint256).max;

        uint256 collateralETH = _userCollateralValueInETH(user);
        if (collateralETH == 0) return 0;
        
        uint256 adjustedCollateral = (collateralETH * LIQ_THRESHOLD_BPS) / 10000;
        return (adjustedCollateral * PRECISION) / debt;
    }

    // Get user's total collateral value in ETH
    function getUserCollateralValueETH(address user) external view returns (uint256) {
        return _userCollateralValueInETH(user);
    }

    // Get user's borrowing capacity
    function getUserBorrowCapacity(address user) external view returns (uint256) {
        uint256 collateralValueETH = _userCollateralValueInETH(user);
        return (collateralValueETH * LTV_BPS) / 10000;
    }

    // Get available borrowing amount for user
    function getAvailableBorrow(address user) external view returns (uint256) {
        uint256 capacity = this.getUserBorrowCapacity(user);
        uint256 currentDebt = borrowBalances[user];
        return capacity > currentDebt ? capacity - currentDebt : 0;
    }

    // Get list of supported collateral tokens
    function getCollateralList() external view returns (address[] memory) {
        return collateralList;
    }

    // Get pool utilization rate
    function getUtilizationRate() external view returns (uint256) {
        uint256 totalSupply = dETHToken.totalSupply();
        if (totalSupply == 0) return 0;
        
        uint256 poolBalance = address(this).balance;
        if (poolBalance >= totalSupply) return 0;
        
        uint256 borrowed = totalSupply - poolBalance;
        return (borrowed * PRECISION) / totalSupply;
    }

    // Internal function to calculate user's total collateral value in ETH
    function _userCollateralValueInETH(address user) internal view returns (uint256 totalETH) {
        for (uint256 i = 0; i < collateralList.length; i++) {
            address token = collateralList[i];
            if (!supportedCollateral[token]) continue;

            uint256 amount = collateralBalance[user][token];
            if (amount == 0) continue;
            
            totalETH += _tokenValueInETH(token, amount);
        }
    }

    // Internal function to get token value in ETH
    function _tokenValueInETH(address token, uint256 amount) internal view returns (uint256) {
        AggregatorV3Interface tokenFeed = priceFeed[token];
        require(address(tokenFeed) != address(0), "No price feed for token");

        (uint80 roundId, int256 tokenUsd, , uint256 updatedAt, ) = tokenFeed.latestRoundData();
        require(tokenUsd > 0, "Invalid token price");
        require(block.timestamp - updatedAt <= PRICE_TIMEOUT, "Price data too old");

        (, int256 ethUsd, , uint256 ethUpdatedAt, ) = ethUsdFeed.latestRoundData();
        require(ethUsd > 0, "Invalid ETH price");
        require(block.timestamp - ethUpdatedAt <= PRICE_TIMEOUT, "ETH price data too old");
        
        uint8 tokenUsdDecimals = tokenFeed.decimals();
        uint8 ethUsdDecimals = ethUsdFeed.decimals();
        
        // Scale prices to 18 decimals
        uint256 tokenUsdScaled = uint256(tokenUsd) * (10 ** (18 - tokenUsdDecimals));
        uint256 ethUsdScaled = uint256(ethUsd) * (10 ** (18 - ethUsdDecimals));
        
        // Scale token amount to 18 decimals
        uint8 tokenDecimals = IERC20Metadata(token).decimals();
        uint256 amountScaled = amount * (10 ** (18 - tokenDecimals));
        
        // Calculate value in ETH
        uint256 valueETH = (amountScaled * tokenUsdScaled) / ethUsdScaled;
        return valueETH;
    }

    // Safe ETH transfer with gas limit
    function _safeTransferETH(address to, uint256 amount) internal {
        require(to != address(0), "Cannot transfer to zero address");
        (bool success, ) = to.call{value: amount, gas: 2300}("");
        require(success, "ETH transfer failed");
    }

    // Receive function for ETH deposits
    receive() external payable {
        // ETH sent directly to contract goes to the pool
    }

    // Get contract's ETH balance
    function getPoolBalance() external view returns (uint256) {
        return address(this).balance;
    }

    // Get total dETH supply
    function getTotalSupply() external view returns (uint256) {
        return dETHToken.totalSupply();
    }

    // Emergency functions for admin
    function emergencyPause() external onlyOwner {
        // Implementation for emergency pause could go here
        // For now, owner can use the existing access controls
    }

    // Get latest price for a token in USD (scaled to 18 decimals)
    function getTokenPriceUSD(address token) external view returns (uint256) {
        require(supportedCollateral[token], "Token not supported");
        
        AggregatorV3Interface tokenFeed = priceFeed[token];
        (, int256 price, , uint256 updatedAt, ) = tokenFeed.latestRoundData();
        
        require(price > 0, "Invalid price");
        require(block.timestamp - updatedAt <= PRICE_TIMEOUT, "Price data too old");
        
        uint8 decimals = tokenFeed.decimals();
        return uint256(price) * (10 ** (18 - decimals));
    }

    // Get ETH price in USD (scaled to 18 decimals)
    function getETHPriceUSD() external view returns (uint256) {
        (, int256 price, , uint256 updatedAt, ) = ethUsdFeed.latestRoundData();
        
        require(price > 0, "Invalid ETH price");
        require(block.timestamp - updatedAt <= PRICE_TIMEOUT, "ETH price data too old");
        
        uint8 decimals = ethUsdFeed.decimals();
        return uint256(price) * (10 ** (18 - decimals));
    }
}

contracts/Lock.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.20;

// Uncomment this line to use console.log
// import "hardhat/console.sol";

contract Lock {
  uint public unlockTime;
  address payable public owner;

  event Withdrawal(uint amount, uint when);

  constructor(uint _unlockTime) payable {
    require(
      block.timestamp < _unlockTime,
      "Unlock time should be in the future"
    );

    unlockTime = _unlockTime;
    owner = payable(msg.sender);
  }

  function withdraw() public {
    // Uncomment this line, and the import of "hardhat/console.sol", to print a log in your terminal
    // console.log("Unlock time is %o and block timestamp is %o", unlockTime, block.timestamp);

    require(block.timestamp >= unlockTime, "You can't withdraw yet");
    require(msg.sender == owner, "You aren't the owner");

    emit Withdrawal(address(this).balance, block.timestamp);

    owner.transfer(address(this).balance);
  }
}

